package Program1;

public class AssingementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int f=7;
	System.out.println("f+=3:"+(f+=3));
	System.out.println("f-=3:"+(f-=3));
	System.out.println("f*=3:"+(f*=3));
	System.out.println("f/=3:"+(f/=3));
	System.out.println("f%=3:"+(f%=3));


	}

}
